        public int[] CHECKBOX_ARRAY
        {
            set
            {
                if (value!=null)
                {
                    //
                    // We go through the array and make the names of apropriate chekboxes
                    //
                    foreach (int chk in value)
                    {
                        string chkname = "checkbox" + chk.ToString();

                        try
                        {
                            //
                            // A little trick to use a name of a Control to define actual chekbox that should be checked
                            //
                            (Controls[chkname] as System.Windows.Forms.CheckBox).Checked = true;
                        }
                        catch (System.Exception ex)
                        {
                            System.Windows.Forms.MessageBox.Show(ex.Message);

                        }

                    }
                }
                
            }
            get
            {
                //
                // Here we use List to collect all checked checkboxes! Every checkbox has a tag e.g. CheckBox1 has Tag = 1 etc
                //
                System.Collections.Generic.List<int> chks = new System.Collections.Generic.List<int>();
                foreach(System.Windows.Forms.Control chk in this.Controls)
                {
                    if(chk is System.Windows.Forms.CheckBox)
                    {
                        if ((chk as System.Windows.Forms.CheckBox).Checked) chks.Add(System.Convert.ToInt32(chk.Tag));

                    }
                }
                
                return chks.ToArray();
            }
        }
